        public function install() {

            // Install SQL
            include(dirname(__FILE__).'/sql/install.php');
            foreach ($sql as $s)
                if (!Db::getInstance()->execute($s))
                            return false;
            if (!Configuration::updateValue('FIELDSLS_STATUS', 1) OR
                !Configuration::updateValue('FIELDSLS_SLWIDTH', 1920) OR
                !Configuration::updateValue('FIELDSLS_SLHEIGHT', 583) OR
                !Configuration::updateValue('FIELDSLS_AUTOPLAY', 1) OR
                !Configuration::updateValue('FIELDSLS_SHOWBUTTON', 1) OR
                !Configuration::updateValue('FIELDSLS_PAUSEONHOVER', 1) OR
                !Configuration::updateValue('FIELDSLS_DELAY', 3000) OR
                !Configuration::updateValue('FIELDSLS_SLTRANSITIONS', 'fade,rotatezoomminusoutb,clip&jumpin,clip&jumpout,expandstairs,swingoutsideoutstairs') OR
                !Configuration::updateValue('FIELDSLS_SLTRANSITIONSRAND', 1) OR
                !Configuration::updateValue('FIELDSLS_CLASSSFX', ''))
            {
                return false;
            }
            // Set some defaults
            return parent::install() && $this->_createTab()
                                     && $this->registerHook('actionObjectSlideshowAddAfter')
                                     && $this->_installHookCustomer()
                                     && $this->registerHook('fieldSlideShow')
                                     && $this->registerHook('displayHeader');

	}
        
        public function uninstall() {
		Configuration::deleteByName('FIELDSLS_STATUS');
		Configuration::deleteByName('FIELDSLS_SLWIDTH');
		Configuration::deleteByName('FIELDSLS_SLHEIGHT');
		Configuration::deleteByName('FIELDSLS_AUTOPLAY');
		Configuration::deleteByName('FIELDSLS_SHOWBUTTON');
		Configuration::deleteByName('FIELDSLS_PAUSEONHOVER');
		Configuration::deleteByName('FIELDSLS_DELAY');
		Configuration::deleteByName('FIELDSLS_SLTRANSITIONS');
		Configuration::deleteByName('FIELDSLS_SLTRANSITIONSRAND');
		Configuration::deleteByName('FIELDSLS_CLASSSFX');
		//uninstall db
                include(dirname(__FILE__).'/sql/uninstall_sql.php');
		foreach ($sql as $s)
			if (!Db::getInstance()->execute($s))
				return false;
		// Uninstall Module
		if (!parent::uninstall())
			return false;
		$this->_deleteTab();
		return true;
        }
        
	//  CREATE THE TAB MENU
	private function _createTab() {
		$response = true;

		// First check for parent tab
		$parentTabID = Tab::getIdFromClassName('AdminFieldMenu');

		if ($parentTabID) {
			$parentTab = new Tab($parentTabID);
		} else {
			$parentTab = new Tab();
			$parentTab->active = 1;
			$parentTab->name = array();
			$parentTab->class_name = "AdminFieldMenu";
			foreach (Language::getLanguages() as $lang){
				$parentTab->name[$lang['id_lang']] = "fieldthemes";
			}
			$parentTab->id_parent = 0;
			$parentTab->module = '';
			$response &= $parentTab->add();
		}
// Check for parent tab2
			$parentTab_2ID = Tab::getIdFromClassName('AdminFieldMenuSecond');
			if ($parentTab_2ID) {
				$parentTab_2 = new Tab($parentTab_2ID);
			}
			else {
				$parentTab_2 = new Tab();
				$parentTab_2->active = 1;
				$parentTab_2->name = array();
				$parentTab_2->class_name = "AdminFieldMenuSecond";
				foreach (Language::getLanguages() as $lang) {
					$parentTab_2->name[$lang['id_lang']] = "FieldThemes Configure";
				}
				$parentTab_2->id_parent = $parentTab->id;
				$parentTab_2->module = '';
				$response &= $parentTab_2->add();
			}
			// Created tab
		$tab = new Tab();
		$tab->active = 1;
		$tab->class_name = "AdminFieldSlideshow";
		$tab->name = array();
		foreach (Language::getLanguages() as $lang){
			$tab->name[$lang['id_lang']] = "Manage Slideshow";
		}
		$tab->id_parent = $parentTab_2->id;
		$tab->module = $this->name;
		$response &= $tab->add();

		return $response;
	}
        
	private function _deleteTab() {
		$id_tab = Tab::getIdFromClassName('AdminFieldSlideshow');
		if($id_tab){
			$tab = new Tab($id_tab);
			$tab->delete();
		}
		// Get the number of tabs inside our parent tab
        // If there is no tabs, remove the parent
		$parentTab_2ID = Tab::getIdFromClassName('AdminFieldMenuSecond');
		if($parentTab_2ID){
			$tabCount_2 = Tab::getNbTabs($parentTab_2ID);
			if ($tabCount_2 == 0) {
				$parentTab_2 = new Tab($parentTab_2ID);
				$parentTab_2->delete();
			}
		}
        // Get the number of tabs inside our parent tab
        // If there is no tabs, remove the parent
		$parentTabID = Tab::getIdFromClassName('AdminFieldMenu');
		if($parentTabID){
			$tabCount = Tab::getNbTabs($parentTabID);
			if ($tabCount == 0){
				$parentTab = new Tab($parentTabID);
				$parentTab->delete();
			}
		}
        return true;
	}
        
        public function getConfigFieldsValues() {
		$values = array();
		$values['FIELDSLS_STATUS'] = Tools::getValue('FIELDSLS_STATUS', Configuration::get('FIELDSLS_STATUS'));
		$values['FIELDSLS_SLWIDTH'] = (int)Tools::getValue('FIELDSLS_SLWIDTH', Configuration::get('FIELDSLS_SLWIDTH'));
		$values['FIELDSLS_SLHEIGHT'] = (int)Tools::getValue('FIELDSLS_SLHEIGHT', Configuration::get('FIELDSLS_SLHEIGHT'));
		$values['FIELDSLS_AUTOPLAY'] = Tools::getValue('FIELDSLS_AUTOPLAY', Configuration::get('FIELDSLS_AUTOPLAY'));
		$values['FIELDSLS_SHOWBUTTON'] = Tools::getValue('FIELDSLS_SHOWBUTTON', Configuration::get('FIELDSLS_SHOWBUTTON'));
		$values['FIELDSLS_PAUSEONHOVER'] = Tools::getValue('FIELDSLS_PAUSEONHOVER', Configuration::get('FIELDSLS_PAUSEONHOVER'));
		$values['FIELDSLS_DELAY'] = (int)Tools::getValue('FIELDSLS_DELAY', Configuration::get('FIELDSLS_DELAY'));
		$values['FIELDSLS_CLASSSFX'] = Tools::getValue('FIELDSLS_CLASSSFX', Configuration::get('FIELDSLS_CLASSSFX'));
		$values['FIELDSLS_SLTRANSITIONS[]'] = Tools::getValue('FIELDSLS_SLTRANSITIONS', explode(',' ,Configuration::get('FIELDSLS_SLTRANSITIONS')));
		$values['FIELDSLS_SLTRANSITIONSRAND'] = Tools::getValue('FIELDSLS_SLTRANSITIONSRAND', Configuration::get('FIELDSLS_SLTRANSITIONSRAND'));
		
		return $values;
	}
	
        public function convert2Value($str) {
		$str = strtolower($str);
		$x = array(" ", "+", "-");
		$y = array("", "plus", "minus");
		return str_replace($x, $y, $str);
	}
        
	public function getListSlideTransitions() {
		$values = array();
		foreach($this->_transitions as $transition) {
			$values[] = array(
				'id' => $this->convert2Value($transition['name']),
				'name' => $transition['name']
			);
		}
		return $values;
	}
        
	public function getContent() {
                $output ='';
		if (Tools::isSubmit('submitFieldSlideshow')) {
                        Configuration::updateValue('FIELDSLS_STATUS', Tools::getValue('FIELDSLS_STATUS'));
                        Configuration::updateValue('FIELDSLS_SLWIDTH', (int)Tools::getValue('FIELDSLS_SLWIDTH'));
                        Configuration::updateValue('FIELDSLS_SLHEIGHT', (int)Tools::getValue('FIELDSLS_SLHEIGHT'));
                        Configuration::updateValue('FIELDSLS_AUTOPLAY', Tools::getValue('FIELDSLS_AUTOPLAY'));
			Configuration::updateValue('FIELDSLS_SHOWBUTTON', Tools::getValue('FIELDSLS_SHOWBUTTON'));
                        Configuration::updateValue('FIELDSLS_PAUSEONHOVER', Tools::getValue('FIELDSLS_PAUSEONHOVER'));

                        Configuration::updateValue('FIELDSLS_DELAY', (int)Tools::getValue('FIELDSLS_DELAY'));
                        Configuration::updateValue('FIELDSLS_CLASSSFX', Tools::getValue('FIELDSLS_CLASSSFX'));

                        Configuration::updateValue('FIELDSLS_SLTRANSITIONS', (string)implode(',', Tools::getValue('FIELDSLS_SLTRANSITIONS')));
                        Configuration::updateValue('FIELDSLS_SLTRANSITIONSRAND', Tools::getValue('FIELDSLS_SLTRANSITIONSRAND'));

                        $this->html .= $this->displayConfirmation($this->l('Settings updated'));
                        $output = $this->displayConfirmation($this->l('Setting updated.'));
                }
                
                return $output.$this->renderForm();
        }
        
