        public function getSlideshowTransitions () {
		$return = '';
		$values_arr = explode(',' ,Configuration::get('FIELDSLS_SLTRANSITIONS'));
		foreach ( $this->_transitions as $trans ) {
			if(in_array($this->convert2Value($trans['name']), $values_arr)){
				$return .= $trans['code'] . ',';
			}
		}
		return preg_replace('/,$/', '', $return);
	}
        
