        public function getSlideshow() {
		
                        $id_shop = (int)Context::getContext()->shop->id;
						$id_lang = (int)$this->context->language->id;
                        $sql = 'SELECT * FROM `' . _DB_PREFIX_ . 'fieldslideshow` ps'; 
						$sql .= ' LEFT JOIN `'. _DB_PREFIX_ . 'fieldslideshow_lang` psl';
						$sql .= ' ON ps.id_fieldslideshow = psl.id_fieldslideshow';
                        $sql .= ' LEFT JOIN `' . _DB_PREFIX_ . 'fieldslideshow_shop`  pss ';
						$sql .= ' ON ps.id_fieldslideshow = pss.id_fieldslideshow';
						$sql .= ' where pss.`id_shop` ='.$id_shop ;
						$sql .= ' AND psl.`id_lang` ='.$id_lang ;
						$sql .=' AND ps.`active` =1';
						$sql .= ' ORDER BY `porder` ASC';
                        $slides = Db::getInstance()->ExecuteS($sql);
                        
                        if(is_array($slides)){
                            $arraySlides = array();
                            foreach($slides  as $key => $slideArray) {
                                $newSlide = array();
                                 foreach($slideArray as $k => $v) {
                                     if($k=='image'){
                                        $v = _PS_BASE_URL_.__PS_BASE_URI__.'modules/fieldslideshow/images/'.$slideArray['images'];
                                        if(isset($_SERVER['HTTPS']) && $_SERVER['HTTPS'] === 'on')
                                           $v = str_replace("http://", "https://", $v);
                                     }
                                     $newSlide[$k] = $v;
                                 }
                                 $arraySlides[$key] = $newSlide;
                            }

                        }
                        return $arraySlides;
        }
        
