                        'FIELDSLS_SLIDESHOW'=> $slides,
                        'FIELDSLS_STATUS' => Configuration::get('FIELDSLS_STATUS'),
                        'FIELDSLS_SLWIDTH' => (int)Configuration::get('FIELDSLS_SLWIDTH'),
                        'FIELDSLS_SLHEIGHT' => (int)Configuration::get('FIELDSLS_SLHEIGHT'),
                        'FIELDSLS_AUTOPLAY' => Configuration::get('FIELDSLS_AUTOPLAY'),
                        'FIELDSLS_SHOWBUTTON' => Configuration::get('FIELDSLS_SHOWBUTTON'),
                        'FIELDSLS_PAUSEONHOVER' => Configuration::get('FIELDSLS_PAUSEONHOVER'),
                        'FIELDSLS_DELAY' => (int)Configuration::get('FIELDSLS_DELAY'),

                        'FIELDSLS_CLASSSFX' => Configuration::get('FIELDSLS_CLASSSFX'),
